import React, {useContext} from "react"
import { SmurfContext } from "./contexts/Context"

export const SmurfList = ({ smurfs }) => {
    useContext(SmurfContext)
    return (
        <div>
            {smurfs.map(smurf => {
                return (
                    <div className='smurf' key={smurf.id}>
                        <strong>{smurf.name}</strong>
                        <p>{smurf.age}</p>
                        <p>{smurf.height}</p>
                    </div>
                )
            })}
        </div>
    )
}
