import React, {useContext} from "react"
import { SmurfContext } from "./contexts/Context"

export const SmurfList = () => {
const {smurfs, deleteSmurf} = useContext(SmurfContext)


return ( 
    smurfs.map(smurf => {
    return (
    <div key={smurf.id}>
    <h1>
    Name: {smurf.name}
    </h1>
    <h3>
    Height: {smurf.height}
    </h3>
    <h3>
    Age: {smurf.age}
    </h3>
    <button onClick={e => { 
        deleteSmurf(e, smurf)
        }}>Delete</button>
    </div>
    )
    })
)
}
